'use strict';

(function() {

    var graph = new joint.dia.Graph;
    var paper = new joint.dia.Paper({
        el: $('#paper').css('width', 800),
        width: 800,
        height: 600,
        gridSize: 1,
        model: graph
    });

    var stencil = new joint.ui.Stencil({ 
        graph: graph, 
        paper: paper,
        width: 200,
        height: 300,
        groups: {
            simple: { label: 'Simple', index: 1 },
            text: { label: 'Text', index: 2 },
            advanced: { label: 'Advanced', index: 3 }
        },
        search: {
            '*': ['attrs/text/text'],
            'basic.Image': ['description'],
            'basic.Path': ['description']
        }
    });

    $('#stencil-container').append(stencil.render().el);

    var r = new joint.shapes.basic.Rect({ 
        position: { x: 10, y: 10 }, size: { width: 70, height: 40 },
        attrs: { rect: { fill: '#31D0C6', stroke: '#4B4A67', 'stroke-width': 8 }, text: { text: 'rect', fill: 'white' } }
    });
    var c = new joint.shapes.basic.Circle({ 
        position: { x: 100, y: 10 }, size: { width: 70, height: 40 },
        attrs: { circle: { fill: '#FE854F', 'stroke-width': 8, stroke: '#4B4A67' }, text: { text: 'ellipse', fill: 'white' } }
    });
    var t = new joint.shapes.basic.Text({
        position: { x: 10, y: 10 }, size: { width: 50, height: 30 },
        attrs: { text: { text: 'Text' } }
    });
    var p = new joint.shapes.basic.Path({
        position: { x: 10, y: 10 }, size: { width: 50, height: 50 },
        attrs: {
            path: {
                fill: '#FEB663',
                d: 'M16,1.466C7.973,1.466,1.466,7.973,1.466,16c0,8.027,6.507,14.534,14.534,14.534c8.027,0,14.534-6.507,14.534-14.534C30.534,7.973,24.027,1.466,16,1.466z M14.757,8h2.42v2.574h-2.42V8z M18.762,23.622H16.1c-1.034,0-1.475-0.44-1.475-1.496v-6.865c0-0.33-0.176-0.484-0.484-0.484h-0.88V12.4h2.662c1.035,0,1.474,0.462,1.474,1.496v6.887c0,0.309,0.176,0.484,0.484,0.484h0.88V23.622z'
            }
        },
        description: 'info'
    });
    var i = new joint.shapes.basic.Image({
        position: { x: 70, y: 10 },
        size: { width: 50, height: 50 },
        attrs: {
            image: { width: 50, height: 50, 'xlink:href': 'data:image/png;base64,' }
        },
        description: 'image'
    });

    stencil.load([r, c], 'simple');
    stencil.load([t], 'text');
    stencil.load([p, i], 'advanced');

    stencil.on('filter', function(graph, group) {
        console.log(graph, group);
    });
    
})();
